import { useEffect, useRef, useState } from "react";
import dayjs from "dayjs";

import relativeTime from 'dayjs/plugin/relativeTime';
dayjs.extend(relativeTime);

export default function ChatScreen () {
    const intRef = useRef(null);
    const [finished, setFinished] = useState(false);
    const [userName, setUserName] = useState('Gonchig');
    const [text, setText] = useState ('');
    const [chats, setChats] = useState ([]);

    useEffect(() => {
        fetch('http://192.168.1.49:3005/')
        .then((res) => res.json ())
        .then((data) => {
            setChats(data.body.reserve());
            setFinished(true);
        });
    }, []);

    useEffect (() => {
        if(finished) {
            clearInterval(intRef.current);
            intRef.current = setInterval(() => {
                fetch('http://192.168.1.49:3005/api/chats/last')
                .then((res) => res.json())
                .then((data) => {
                    setChats([data.body, ...chats]);
                });
            }, 500);

        }
    }, [finished]);

    const send = (e) => {
        e.preventDefault();
        if (text === '') {
            alert('hooson zurvas bichih bolomjgui!');
            return;
        }
        fetch('http://192.168.1.49:3005/api/chats', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({name: userName, text }),
        })
        .then((res) => res.json())
        .then((data) => {
            console.log(data);
        });
    };

    return (
        <div className="container">
        <div className="row justify-content-center">
            <div className="col-sm-6">
                <div className="chat-container">
                    <div className="wrapper">
                        <ul>
                            {chats.map((chat) => (
                                <ChatItem chat={chat} key={chat.id}/>
                            ))}
                        </ul>
                    </div>
                    <form onSubmit={send}>
                        <textarea 
                        value={text}
                        onChange={(e) => {
                            setText(e.target.value);
                        }}/>
                        <button>
                            Send
                        </button>
                    </form>
                </div>
            </div>
        </div>
    </div>
    );
}

const ChatItem = ({ chat }) => {
    return (
        <li className="chat-item">
            <div className="text">
                <strong className="author">{chat.name}:</strong> {chat.text}
            </div>
        </li>
    )
}
